import './App.css'
import {useEffect, useState} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.js';

const MOCK_API_URL = 'https://645e56dd12e0a87ac0edd618.mockapi.io/projectweek15'

function App() {

const [customers, setCustomers] = useState([{}])

const [newCustomerName, setNewCustomerName] = useState('')
const [newDogType, setNewDogType] = useState('')
const [newCustomerAdoptionDate, setNewCustomerAdoptionDate] = useState('')

const [updatedName,setUpdatedName] = useState('')
const [updatedDogType, setUpdatedDogType] = useState('')
const [updatedAdoptionDate, setUpdatedAdoptionDate] = useState('')

function getCustomers(){
  fetch(MOCK_API_URL)
  .then(data => data.json())
  .then(data => setCustomers(data))
}

useEffect(() => {
  getCustomers()
  console.log(customers)
}, [])

function deleteCustomer(id){
  fetch(`${MOCK_API_URL}/${id}`, {
    method: 'DELETE'
  }).then(() => getCustomers())

}

function postNewCustomer(e){
  e.preventDefault()

  fetch(MOCK_API_URL, {
    method: 'POST',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
      name: newCustomerName,
      dogType: newDogType,  
      adoptionDate: newCustomerAdoptionDate,
    })
  }).then(() => getCustomers())
} 

function updateCustomer(e, customerObject){
  e.preventDefault()

  let updatedCustomerObject = {
    ...customerObject, 
    name: updatedName, 
    dogType: updatedDogType, 
    adoptionDate: updatedAdoptionDate,
  }

  fetch(`${MOCK_API_URL}/${customerObject.id}`, {
    method: 'PUT', 
    body: JSON.stringify(updatedCustomerObject),
    headers: {
      "Content-Type": "application/json"
    }
  }).then(() => getCustomers())
}



  return (
    <div className="App">
      <img src='bone'></img>
      <form>
        <h1>Dog Adoption Database</h1>
        <br />
        <label>Name:</label>
        <input onChange={(e) => setNewCustomerName(e.target.value)}></input>
        <label>Job Title:</label>
        <input onChange={(e) => setNewDogType(e.target.value)}></input>
        <label>Adoption Date:</label>
        <input type="date" onChange={(e) => setNewCustomerAdoptionDate(e.target.value)}></input>
        <button onClick={(e) => postNewCustomer(e)}>Submit</button>
      </form>

      {customers.map((customer, index) => (
        <div className='customerContainer' key={index}>
          <h3>Current Information:</h3>
            <div>
              Customer Full Name: {customer.name} <br />
              Dog Type: {customer.dogType} <br />
              Adoption Date Requested: {customer.adoptionDate} 
              <br />
              <br />
              <button onClick={() => deleteCustomer(customer.id)}>Delete</button>
              <br />
              <br />
            </div>

            <form>
              <h3>Update Customer Credentials</h3>
              <label>Update Customer Name:</label>
              <input onChange={(e) => setUpdatedName(e.target.value)}></input><br />             
              <label>Update Dog Type:</label>
              <input onChange={(e) => setUpdatedDogType(e.target.value)}></input><br />
              <label>Change Adoption Date:</label>
              <input type="date" onChange={(e) => setUpdatedAdoptionDate(e.target.value)}></input>
              <br /> 
              <br />
              <button onClick={(e) => updateCustomer(e, customer)}>Update</button>
              <br />
            </form>
        </div>
      ))}
    </div>
  )
}

export default App

/**
 *     While this lab focused mostly on functionality over practicality,
 *     here's a couple ideas you can try implementing to make the UI/UX more user-friendly:
 *
 *    1: Having a form laid out for EVERY user isn't very UI friendly, nor realistic.
 *       Consider using a styling library like Bootstrap React/MaterialUI/SemanticUI to put
 *       each of those forms in a modal. The form inside the modal only pops up when a
 *       "update user" button gets clicked, and only updates the user when that modal gets submitted.
 *
 * Docs: https://react-bootstrap.github.io/components/modal/
 *
 *
 *    2: Organize the users somehow. You can use a table, CSS grid/flex.
 *       Possibly put all the users in a container, and look at the CSS property overflow:
 *
 * Docs: https://developer.mozilla.org/en-US/docs/Web/CSS/overflow
 *
 */
